 Image arrifulGetBase64StringToBitmap(String Base64StringGet)
{
  Uint8List uint8List = base64Decode(Base64StringGet);
  Image image = Image.memory(
    uint8List,
    fit: BoxFit.cover, // Adjust the fit as needed
  );
  return image;

}

